Future<List<File>> pickImagesDeterministic({int limit = 10}) async {
  final picker = ImagePicker();
  final picks = await picker.pickMultiImage(limit: limit);
  final tmp = await getTemporaryDirectory();

  final files = <File>[];
  for (final x in picks) {
    final Uint8List bytes = await x.readAsBytes();
    final decoded = img.decodeImage(bytes);
    if (decoded == null) continue;

    // Normalize: bake EXIF orientation and encode to PNG (deterministic).
    final baked = img.bakeOrientation(decoded);
    final normalizedPng = img.encodePng(baked);

    // Optional: stable name by content hash.
    final hash = crypto.sha1.convert(normalizedPng).toString();
    final f = File('${tmp.path}/$hash.png');
    await f.writeAsBytes(normalizedPng, flush: true);
    files.add(f);
  }
  return files;
}
